     setBarcode(barcode);
     setPName(name);
     setPrice(price);
     setUID(uid);
     setReturnable(returnable);
  }


  const showAlert = (msg, func, actionName)=>{

    confirmAlert({
    message: msg,
    buttons: [
        {
          label: actionName,
          onClick: func  
        },                   
        {
          label: 'Cancel',
          //onClick: () => alert('Click No')
        }
     ]
    });
  }

  const clickFileInput = ()=>{
    let input = document.querySelector("#photo-input");
    input.click();
  }

